sysauto_script.script:
(function () {

    // ------------------------------
    // Defaults (adjust if needed)
    // ------------------------------
    var LOOKBACK_DAYS = 100;   // last 100 days of closed changes
    var MAX_TO_QUEUE  = 1000; // cap per run to avoid huge bursts
    var DRY_RUN       = false; // set false once you confirm it queues correctly

    gs.info('[changeBatchSync][SCHEDULED] Starting. lookbackDays=' + LOOKBACK_DAYS +
        ' maxToQueue=' + MAX_TO_QUEUE + ' dryRun=' + DRY_RUN);

    var sync = new x_xma_eb_fd.changeBatchSync();
    sync.runBatch({
        lookbackDays: LOOKBACK_DAYS,
        maxToQueue: MAX_TO_QUEUE,
        dryRun: DRY_RUN
    });

    gs.info('[changeBatchSync][SCHEDULED] Done.');

})();
